import React, { useState, useEffect } from 'react'
import './Feed.css';
import gsap from 'gsap';
import {useGSAP} from '@gsap/react';
import Message from '../components/Message';
import NewMessage from '../components/NewMessage';



function Feed() {
  gsap.registerPlugin(useGSAP);
  const[posts, setPosts] = useState([{}])
  const[newMessageTl] = useState(gsap.timeline({repeat: 0, paused:true}))

  useGSAP(() => {
    newMessageTl.to(".new-message-container", {display:'flex'});
    newMessageTl.fromTo(".new-message-window", {opacity:0}, {opacity:1}, '<');
  })

  const openNewMessageWindow = () => {
    newMessageTl.play();
  }

  const closeNewMessageWindow = () => {
    console.log("closing message window");
    newMessageTl.reverse();
  }

  useEffect(() => {
    fetch('./posts').then(
      response => response.json()
    ).then(
      data => {
        setPosts(data);
      }
    )
  }, [])

  return (
    <div className='feed-background'>
        <div className='top-row'>
          <div className='top-row-bar'></div>
        </div>
        <div className='feed-container'>
            <div className='feed'>
              {(typeof posts === 'undefined') ? (
                <div className='feed-loading'>
                  <h1>feed loading...</h1>
                </div>
              ) : (
                (posts.map((post, i) => (
                  <Message key={i}
                    post = {post}
                  />
                )))
              )}
            </div>
        </div>
        <div className='bottom-row'>
            <div className='new-message-button' onClick={(e) => openNewMessageWindow()}>
                <h1>New Message</h1>
            </div>
        </div>
        <NewMessage closeNewMessageWindow={closeNewMessageWindow}/>
    </div>
  )
}

export default Feed
