import React, { useEffect } from 'react'
import './NewMessage.css'

function NewMessage({newMessageTl}) {

        const closeNewMessageWindow = (e : React.MouseEvent) => {
                console.log("closing message window");
                if(e.target == document.getElementsByClassName('new-message-container')){
                        newMessageTl.reverse();
                }
        }

        return (
                <div className='new-message-container' onClick={(e) => closeNewMessageWindow(e)}>
                        <div className='new-message-window'>
                                <div className='new-message-internals'>
                                        <h3>New Message</h3>
                                        <textarea
                                                type="text" 
                                                name="message"  
                                                maxLength="10000"
                                        />
                                        <div className='submit-button'>
                                                <button>Submit</button>
                                        </div>
                                </div>
                        </div>
                </div>
        )
}

export default NewMessage
